create table medicine
(
    id             int auto_increment
        primary key,
    medicine_no    varchar(32)                            not null,
    name           varchar(300)                           not null,
    english_name   varchar(100)                           null,
    type           varchar(32)                            not null,
    unit           varchar(32)                            null comment '单位',
    cell_unit      varchar(32)                            null comment '基础单位',
    cell_num       int(32)                                null comment '每盒中含有cell数量',
    cell_weight    int(32)                                null comment '基础cell的重量',
    each_dose      int(32)                                null comment '每次剂量',
    taking_way     varchar(32)                            null comment '服用方式',
    daily_times    int(32)                                null comment '每日次数',
    status         varchar(32)                            null,
    create_time    datetime default CURRENT_TIMESTAMP     not null comment '创建时间',
    update_time    datetime default '1970-01-01 00:00:00' not null on update CURRENT_TIMESTAMP comment '修改时间',
    memo           varchar(1024)                          null comment '备注',
    spec           varchar(1024)                          null comment '规格',
    unit_str       varchar(1024)                          null comment '手动定义的单位',
    frequency      varchar(32)                            null comment '用药频次',
    medical_advice varchar(32)                            null comment '医嘱',
    constraint medicine_no_uq
        unique (medicine_no)
);

create index medicine_no_name_e_index
    on medicine (medicine_no, name, english_name);

