create table recipe_info
(
    id            int auto_increment
        primary key,
    recipe_no     varchar(32)                            not null,
    recipe_type   varchar(32)                            null,
    patient_name  varchar(100)                           not null,
    patient_sex   tinyint(2)                             not null,
    patient_age   int(3)                                 not null,
    pay_type      varchar(32)                            null,
    disease       varchar(2048)                          not null,
    classfication varchar(300)                           null comment '科别',
    num           int                                    null,
    status        varchar(32)                            null,
    create_time   datetime default CURRENT_TIMESTAMP     not null comment '创建时间',
    update_time   datetime default '1970-01-01 00:00:00' not null on update CURRENT_TIMESTAMP comment '修改时间',
    age_type      varchar(30)                            null,
    constraint recipe_info_no_uq
        unique (recipe_no)
);

create index recipe_info_no_name_index
    on recipe_info (recipe_no, patient_name);

