mapper.select:
select
*
from recipe_template


    id = #{id,jdbcType=INTEGER}


   and  recipeTemplate_no =  #{recipeTemplateNo,jdbcType=VARCHAR}


    and recipe_type = #{recipeType,jdbcType=VARCHAR}


    and disease = #{disease,jdbcType=VARCHAR}


    and classfication = #{classfication,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}




  order by ${orderBy}


  limit   #{current,jdbcType=INTEGER} , #{pageSize,jdbcType=INTEGER}

select
count(*)
from recipe_template


    id = #{id,jdbcType=INTEGER}


    and  recipeTemplate_no =  #{recipeTemplateNo,jdbcType=VARCHAR}


    and recipe_type = #{recipeType,jdbcType=VARCHAR}


    and disease = #{disease,jdbcType=VARCHAR}


    and classfication = #{classfication,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}

mapper.update:
UPDATE recipe_template
`recipe_type`=#{recipeType}, disease=#{disease}, `classfication`=#{classfication}, status=#{status}, update_time = current_timestamp(),
WHERE recipeTemplate_no=#{recipeTemplateNo}
